using System;using System.ComponentModel;using WellFired.Guacamole.Annotations;using WellFired.Guacamole.DataBinding;using WellFired.Guacamole.Event;using WellFired.Guacamole.Layouts;using WellFired.Guacamole.Renderer;using WellFired.Guacamole.Styling;using WellFired.Guacamole.Types;namespace WellFired.Guacamole.Views{	public partial class View : BindableObject, IView	{		protected UIRect FinalRenderRect = default(UIRect);		private bool _invalidRectRequest = true;		private INativeRenderer _nativeRenderer;		private Style _style;		private UIRect _validRectRequest;	    protected IView Content { get; set; }	    public View()		{			_validRectRequest = UIRect.Min;		}		public UIRect RectRequest => _validRectRequest;		public string Id { get; set; }		protected INativeRenderer NativeRenderer		{			get			{				if (_nativeRenderer != default(INativeRenderer))					return _nativeRenderer;				INativeRenderer newNativeRenderer;				try				{					newNativeRenderer = NativeRendererHelper.CreateNativeRendererFor(GetType());					if (newNativeRenderer == null)						throw new Exception();				}				catch (Exception)				{					return null;				}				if (_nativeRenderer != null && newNativeRenderer != _nativeRenderer)				{					PropertyChanged -= OnPropertyChanged;					PropertyChanged -= _nativeRenderer.OnPropertyChanged;				}				_nativeRenderer = newNativeRenderer;			    if (_nativeRenderer == null)			        return _nativeRenderer;			    PropertyChanged += _nativeRenderer.OnPropertyChanged;			    PropertyChanged += OnPropertyChanged;			    _nativeRenderer.Control = this;			    _nativeRenderer.Create();			    return _nativeRenderer;			}		}		public virtual void UpdateContextIfNeeded()		{			// If something has set the binding context manually, we shouldn't override it. Otherwise, update it here.			if (BindingContext == null)				BindingContext = BindingContext;		    var layout = this as ICanLayout;		    layout?.DoLayout();		    (Content as View)?.UpdateContextIfNeeded();		}		public virtual void Render(UIRect parentRect)		{			FinalRenderRect.X = parentRect.X + RectRequest.X;			FinalRenderRect.Y = parentRect.Y + RectRequest.Y;			FinalRenderRect.Width = RectRequest.Width;			FinalRenderRect.Height = RectRequest.Height;			NativeRenderer.Render(FinalRenderRect);		    (Content as View)?.Render(FinalRenderRect);		}		public virtual void AttemptToFullfillRequests(UIRect availableSpace)		{			if (HorizontalLayout == LayoutOptions.Fill)				_validRectRequest.Width = availableSpace.Width;			if (VerticalLayout == LayoutOptions.Fill)				_validRectRequest.Height = availableSpace.Height;		    (Content as View)?.AttemptToFullfillRequests(availableSpace - Padding);		}		[PublicAPI]		public virtual void InvalidateRectRequest()		{			_invalidRectRequest = true;		    (Content as View)?.InvalidateRectRequest();		}		public virtual void CalculateRectRequest()		{		    // When calculating size, we want to recurse the whole structure, calculating the size of the Child		    // components first of all.		    (Content as View)?.CalculateRectRequest();		    if (!_invalidRectRequest)				return;			_validRectRequest = CalculateValidRectRequest();			_validRectRequest += Padding;			_invalidRectRequest = false;		}		protected virtual UIRect CalculateValidRectRequest()		{			// If the native renderer returns null, we simply use our own layoutting system.			var nativeSize = NativeRenderer?.NativeSize ?? new UISize(MinSize.Width, MinSize.Height);			// Constrain			nativeSize = Constrain(nativeSize);			return new UIRect(0, 0, nativeSize.Width, nativeSize.Height);		}		private UISize Constrain(UISize requestedSize)		{			if (requestedSize.Width < MinSize.Width)				requestedSize.Width = MinSize.Width;			if (requestedSize.Height < MinSize.Height)				requestedSize.Height = MinSize.Height;			if (requestedSize.Width > MaxSize.Width)				requestedSize.Width = MaxSize.Width;			if (requestedSize.Height > MaxSize.Height)				requestedSize.Height = MaxSize.Height;			return requestedSize;		}		internal void LayoutTo(int x, int y)		{			_validRectRequest.X = x;			_validRectRequest.Y = y;		}		internal void ReAdjustTo(int width, int height)		{			_validRectRequest.Width = width;			_validRectRequest.Height = height;		}		protected override void OnPropertyChanged(object sender, PropertyChangedEventArgs e)		{			base.OnPropertyChanged(sender, e);			ProcessTriggers(e.PropertyName);		    (Content as View)?.OnPropertyChanged(sender, e);		}		private void UpdateNewStyle(Style style)		{			foreach (var setter in style.Setters)				SetValue(setter.Property, setter.Value);		}		private void ProcessTriggers(string propertyName)		{			if (Style == null)				return;			foreach (var trigger in Style.Triggers)			{				if (trigger.Property.PropertyName != propertyName)					continue;				var value = GetValue(trigger.Property);				if (!value.Equals(trigger.Value))					continue;				foreach (var setter in trigger.Setters)					SetValue(setter.Property, setter.Value);			}		}		public void RaiseEvent(IEvent raisedEvent)		{			var clickEvent = raisedEvent as ClickEvent;			if (clickEvent != null)			{				var clickable = this as IClickable;				clickable?.Click(clickEvent.Button);			}			var typeEvent = raisedEvent as TypeEvent;			if (typeEvent == null)				return;			var typeable = this as ITypeable;			typeable?.Type(typeEvent.Key);		}		public void FocusControl()		{			NativeRenderer.FocusControl();		}	}}