using System;using System.ComponentModel;using WellFired.Guacamole.Annotations;using WellFired.Guacamole.DataBinding;using WellFired.Guacamole.Event;using WellFired.Guacamole.Renderer;using WellFired.Guacamole.Styling;using WellFired.Guacamole.Types;namespace WellFired.Guacamole.Views{	public partial class View : BindableObject, IView	{	    protected UIRect FinalRenderRect = default(UIRect);	    private INativeRenderer _nativeRenderer;		private Style _style;		private UIRect _validRectRequest;		private IStyleDictionary _styleDictionary;		public IView Content { get; set; }	    public View()		{			RectRequest = UIRect.Min;		}		public UIRect RectRequest { get { return _validRectRequest; } set { _validRectRequest = value; } }		public UIRect ContentRectRequest { get; set; }		public bool ValidRectRequest { get; set; }	    public string Id { get; set; }	    public INativeRenderer NativeRenderer		{			get			{				if (_nativeRenderer != default(INativeRenderer))					return _nativeRenderer;				INativeRenderer newNativeRenderer;				try				{					newNativeRenderer = NativeRendererHelper.CreateNativeRendererFor(GetType());					if (newNativeRenderer == null)						throw new Exception();				}				catch (Exception)				{					return null;				}				if (_nativeRenderer != null && newNativeRenderer != _nativeRenderer)				{					PropertyChanged -= OnPropertyChanged;					PropertyChanged -= _nativeRenderer.OnPropertyChanged;				}				_nativeRenderer = newNativeRenderer;			    if (_nativeRenderer == null)			        return _nativeRenderer;			    PropertyChanged += _nativeRenderer.OnPropertyChanged;			    PropertyChanged += OnPropertyChanged;			    _nativeRenderer.Control = this;			    _nativeRenderer.Create();			    return _nativeRenderer;			}		}		public virtual void Render(UIRect parentRect)		{		    FinalRenderRect.X = parentRect.X + X;		    FinalRenderRect.Y = parentRect.Y + Y;		    FinalRenderRect.Width = RectRequest.Width;		    FinalRenderRect.Height = RectRequest.Height;			var finalContentRect = FinalRenderRect;			finalContentRect.X += ContentRectRequest.X;			finalContentRect.Y += ContentRectRequest.Y;			finalContentRect.Width = ContentRectRequest.Width;			finalContentRect.Height = ContentRectRequest.Height;		    NativeRenderer.Render(finalContentRect);		    (Content as View)?.Render(finalContentRect);		}		[PublicAPI]		public virtual void InvalidateRectRequest()		{			ValidRectRequest = false;		    (Content as View)?.InvalidateRectRequest();		}	    protected override void OnPropertyChanged(object sender, PropertyChangedEventArgs e)		{			base.OnPropertyChanged(sender, e);			if (e.PropertyName == BindingContextProperty.PropertyName)			{				var view = Content as View;				if (view != null)					view.BindingContext = BindingContext;			}			if(Style != null)				StyleHelper.ProcessTriggers(Style.Triggers, this, e.PropertyName);		    (Content as View)?.OnPropertyChanged(sender, e);		}		private void UpdateNewStyle(Style style)		{			foreach (var setter in style.Setters)				SetValue(setter.Property, setter.Value);		}		public void RaiseEvent(IEvent raisedEvent)		{			var clickEvent = raisedEvent as ClickEvent;			if (clickEvent != null)			{				var clickable = this as IClickable;				clickable?.Click(clickEvent.Button);			}			var typeEvent = raisedEvent as TypeEvent;			if (typeEvent == null)				return;			var typeable = this as ITypeable;			typeable?.Type(typeEvent.Key);		}		public void FocusControl()		{			NativeRenderer.FocusControl();		}		public virtual void SetStyleDictionary(IStyleDictionary styleDictionary)		{			_styleDictionary = styleDictionary;			Content?.SetStyleDictionary(_styleDictionary);			var style = _styleDictionary?.Get(GetType());			if (style == default(Style)) 				return;						Style = style;		}	}}